mapper.insert:
insert into Address 
(
	addr_id, 		user_id, 		person_name, 		addr_provence, 		addr_city, 		addr_country, 		addr_stree, 
	addr_mail, 		phone, 			addr_status,		delete_status
)
VALUES
(
	#{addr_id}, 	#{user_id}, 	#{person_name}, 	#{addr_provence},	#{addr_city},	#{addr_country},	#{addr_stree},
	#{addr_mail},	#{phone},		#{addr_status}		#{delete_status}
)

mapper.update:
update Address	set  delete_status = 1

	,person_name = #{person_name}


	,addr_provence = #{addr_provence}


	,addr_city = #{addr_city}


	,addr_country = #{addr_country}


	,addr_stree = #{addr_stree}


	,addr_mail = #{addr_mail}


	,phone = #{phone}


	,addr_status = #{addr_status}


	,delete_status = #{delete_status}

where 1=1

	and addr_id = #{addr_id}


	and user_id = #{user_id}

mapper.select:
select * from Address
where 1=1

	and user_id = #{user_id}


	and addr_id = #{addr_id}
